import Layout from "../../components/Layout";
import { withRouter } from "next/router";
import { useRouter } from "next/router";
import React, { useState } from "react";
// import { Button } from 'antd';
// import { useEffect } from 'react'
import "../css/newbookdetail.css";

// let bookidtest = 0;
const IndexPage = (props: any) => {
  return (
    <Layout title="详情页">
      <div>
        {/* 头部容器 */}
        <div className="alltitle">
          <div className="box-newbook">
            <div className="detail-newbook">书 本 详 情</div>
          </div>
        </div>
        {/* --------------主体部分-------------------------- */}
        <div className="bodybox">
          <div className="bodycenter">
            <div className="contentleft-newbook">
              {props.show.map((item: any) => {
                return (
                <div className="tedaikey" key={item.book}>
                <div className="bookname-newbook">{item.bookName}</div>
                </div>)
              })}
            </div>
            <div className="contentright-newbook"></div>
          </div>
        </div>
        {/* --------------主体部分结束-------------------------- */}
      </div>
    </Layout>
  );
};

IndexPage.getInitialProps = async (context: any) => {
  // console.log(context.query)
  const { id } = context.query;
  const res = await fetch(`http://localhost:3001/essay/${id}`);
  const data = await res.json();
  //这边返回的数据会被挂载到组件的props中
  console.log(data);
  return { show: data };
};

export default withRouter(IndexPage);
