// import Link from 'next/link'
import Layout from "../components/Layout";
import { withRouter } from "next/router";
// import { useEffect } from "react";
// import { Button } from 'antd';
//开启css模块化之后可以这样使用样式
// import cssobj from './index.less'
import cssobj from "./css/home.less";
// import LunBoComponent from "./components/lunbo";
import "@/static/css/font-awesome.css";
// var data = require("../static/data.json");
// import { Carousel } from "antd";

// 声明的全局数据
var imgnumber: number = 0;

const IndexPage = (props: any) => {
  // console.log(props);
  return (
    <Layout title="首页">
      <header>
        <div className={cssobj.shortcut}>
          <div className={cssobj.headerWrap}>
            <div className={cssobj.sloganBox}>
              <span className={cssobj.text}>网罗天下图书</span>
              <span>传承中华文明</span>
            </div>
          </div>
          <div className={[cssobj.userinfobox, cssobj.clearfix].join(" ")}>
            {/* 1 */}
            <div className={cssobj.itemInfo}>
              <span id="nickName" className="info-normal">
                <span className="info-text">登录</span>
                <span className="info-text">/</span>
                <span className="info-text">注册</span>
              </span>
              <div id="loginWin" className="info-win">
                {" "}
                <div className="login-box">
                  {" "}
                  <div className="login-btn">
                    登&nbsp;&nbsp;&nbsp;&nbsp;录
                  </div>{" "}
                  <div class="register-btn">免费注册</div>{" "}
                  <div class="other-login">
                    {" "}
                    <div class="title">其他方式</div>{" "}
                    <div class="login-list clearfix">
                      {" "}
                      <div class="login-weixin icon-btn" type="weixin">
                        {" "}
                        <div class="icon-box">
                          {" "}
                          <span class="iconfont icon-weixin"></span>{" "}
                        </div>{" "}
                        <div class="text">微信</div>{" "}
                      </div>{" "}
                      <div class="login-qq icon-btn" type="qq">
                        {" "}
                        <div class="icon-box">
                          {" "}
                          <span class="iconfont icon-qq"></span>{" "}
                        </div>{" "}
                        <div class="text">QQ</div>{" "}
                      </div>{" "}
                      <div class="login-weibo icon-btn" type="weibo">
                        {" "}
                        <div class="icon-box">
                          {" "}
                          <span class="iconfont icon-weibo"></span>{" "}
                        </div>{" "}
                        <div class="text">微博</div>{" "}
                      </div>{" "}
                    </div>{" "}
                  </div>{" "}
                </div>{" "}
              </div>
            </div>
            <div className={cssobj.itemInfo}></div>
            <div className={cssobj.itemInfo}></div>
            {/* 2 */}
            <div className={cssobj.itemInfo}></div>
            <div className={cssobj.itemInfo}></div>
            <div className={cssobj.itemInfo}></div>
            {/* 3 */}
            <div className={cssobj.itemInfo}></div>
            <div className={cssobj.itemInfo}></div>
            <div className={cssobj.itemInfo}></div>
          </div>
        </div>
      </header>
    </Layout>
  );
};

//getInitialProps 方法既可以在服务器运行，又可以在客户端运行(组件创建之前执行的)
//当刷新页面(初次载入页面)，该方法会在服务器执行
//当通过<Link>跳转路由的时候，该方法会在客户端执行
IndexPage.getInitialProps = async () => {
  const res = await fetch("http://localhost:3001/api/essaytable");
  const data = await res.json();
  //这边返回的数据会被挂载到组件的props中
  return { show: data };
};

export default withRouter(IndexPage);
