import React, { Component } from "react";
import ReactDOM from "react-dom";
// import "antd/dist/antd.css";
// import "./index.css";
import { Card, Pagination } from "antd";

type IProps = {
  show: [{}];
};
type State = {
  minValue: number;
  maxValue: number;
};

export default class App extends React.Component<IProps, State> {
  static async getInitialProps() {
    const res = await fetch("http://localhost:3001/novel");
    const show = await res.json();
    //这边返回的数据会被挂载到组件的props中
    return { show };
  }
  constructor(props: any) {
    super(props);
    this.state = {
      minValue: 0,
      maxValue: 3,
    };
  }
  handleChange = (value: any) => {
    if (value <= 1) {
      this.setState({
        minValue: 0,
        maxValue: 3,
      });
    } else {
      this.setState({
        minValue: (value - 1) * 3,
        maxValue: (value - 1) * 3 + 3,
      });
    }
  };
  render() {
    let data = {}
    return (
      <div>
        {data &&
          data.length > 0 &&
          data.slice(this.state.minValue, this.state.maxValue).map((val) => (
            <Card title={val.title} style={{ width: 300 }}>
              <p>{val.value}</p>
            </Card>
          ))}
        <Pagination
          defaultCurrent={1}
          defaultPageSize={3}
          onChange={this.handleChange}
          total={data.length}
        />
      </div>
    );
  }
}
