import Layout from "../../components/Layout";
import { withRouter } from "next/router";
import { useRouter } from "next/router";
import React, { useState } from "react";
// import { Button } from 'antd';
// import { useEffect } from 'react'
import "../css/newbookdetail.css";
import cssobj from "../css/newbookdetail2.less";

// let bookidtest = 0;
const IndexPage = (props: any) => {
  return (
    <Layout title="详情页">
      <div className="body-newbookbox">
        {/*----------头部导航栏开始--------------------*/}
        <div id="db-global-nav" className="global-nav">
          <div className="bd">
            <div className="top-nav-info">
              <a className="nav-login" rel="nofollow">
                登录/注册
              </a>
            </div>

            <div className="top-nav-doubanapp">
              <a className="lnk-doubanapp">下载网站客户端</a>
            </div>

            <div className="global-nav-items">
              <ul>
                <li className="">
                  <a target="_blank">孔网</a>
                </li>
                <li className="on">
                  <a>读书</a>
                </li>
                <li className="">
                  <a target="_blank">古籍</a>
                </li>
                <li className="">
                  <a target="_blank">墨迹</a>
                </li>
                <li className="">
                  <a target="_blank">珍本</a>
                </li>
                <li className="">
                  <a target="_blank">小说</a>
                </li>
                <li className="">
                  <a target="_blank">散文</a>
                </li>
                <li className="">
                  <a target="_blank">随笔</a>
                </li>
                <li className="">
                  <a target="_blank">文学</a>
                </li>
                <li className="">
                  <a target="_blank">宗教</a>
                </li>
              </ul>
            </div>
          </div>
        </div>
        {/* ----------头部导航栏结束---------------------*/}
        {/*------书本详情框开始-------------------------- */}
        <div className="bookdetail-ershou">
          <div className="bookdetailbox">
            <div className="detailbook">书本详情</div>
          </div>
        </div>
        {/*------书本详情框结束--------------------------   */}
        {/* --------------主体部分-------------------------- */}
        {props.show.map((item: any) => {
          return (
            <div className="detailbox-jiuwang" key={item.bookName}>
              <div id="wrapper">
                <h1>
                  <span>{item.bookName}</span>
                  <div className="clear"></div>
                </h1>
                {/* --------------------*/}
                <div id="content">
                  <div className="grid-16-8 clearfix">
                    <div className="article">
                         <div className="indent">
                           <div className="subjectwrap clearfix">
                             <div className="subject clearfix">
                               <div id="mainpic">
                                 <a><img  src={`../static/images/image/${item.imgPath}`} className="mainpicimg" /></a>
                               </div>

                               {/* ---- */}
                          
                             </div>
                           </div>
                         </div>
                    </div>
                    {/* -------------------------- */}
                    <div className="aside"></div>
                    <div className="extra"></div>
                  </div>
                </div>
                {/* -------------------- */}
              </div>
            </div>
          );
        })}

        {/* --------------主体部分结束-------------------------- */}
      </div>
    </Layout>
  );
};

IndexPage.getInitialProps = async (context: any) => {
  // console.log(context.query)
  const { id } = context.query;
  const res = await fetch(`http://localhost:3001/essay/${id}`);
  const data = await res.json();
  //这边返回的数据会被挂载到组件的props中
  console.log(data);
  return { show: data };
};

export default withRouter(IndexPage);
