import Link from "next/link";
import Layout from "../../components/Layout";
import { withRouter } from "next/router";
import React, { useState, useRef } from "react";
import "@/static/css/font-awesome.css";
import "./novel.css";
import fetch from "isomorphic-unfetch";
import Topboxnav from "../components/topboxnav"

// 声明的全局数据

const IndexPage = (props: any) => {
  return (
    <Layout title="分类：小说">
      <div className="novel-body">
        <Topboxnav></Topboxnav>
        <div id="db-nav-book" className="nav">
          <div className="nav-wrap"style={{height:"75.677px"}} >
            <div className="nav-primary" style={{height:"74px"}}>
              <div className="nav-logo" style={{width:"145px",height:"56px"}}>
                  <a href="" style={{fontSize:"30px",fontWeight:"bolder"}}>小说分类</a>
              </div>
              <div className="nav-search">
              <form action="https://search.douban.com/book/subject_search" method="get">
        <fieldset>
          <legend>搜索：</legend>
          <label htmlFor="inp-query">
          </label>
          <div className="inp"><input id="inp-query" name="search_text"  placeholder="书名、作者、ISBN" value=""  /></div>
          <div className="inp-btn"><input type="submit" value="搜索" /></div>
          <input type="hidden" name="cat" value="1001" />
        </fieldset>
      </form>
              </div>
            </div>
          </div>
          <div className="nav-secondary"></div>
        </div>
      </div>
    </Layout>
  );
};

// getInitialProps 方法既可以在服务器运行，又可以在客户端运行(组件创建之前执行的)
// 当刷新页面(初次载入页面)，该方法会在服务器执行
// 当通过<Link>跳转路由的时候，该方法会在客户端执行
IndexPage.getInitialProps = async () => {
  const res = await fetch("http://localhost:3001/novel");
  const data = await res.json();
  //这边返回的数据会被挂载到组件的props中
  return { show: data };
};

export default withRouter(IndexPage);
